import React, { Component } from 'react'
import Avatar from 'react-avatar';
import PropTypes from 'prop-types';
import MenuDropdown from './menuDropdown';
import MenuItem from '@material-ui/core/MenuItem';
import Menu from '@material-ui/core/Menu';


class PostItem extends Component {


    state = {
        anchorEl: null,
        selectedIndex: 0,
      };

      handleClickListItem = event => {
        this.setState({ anchorEl: event.currentTarget });
      };

      handleMenuItemClick = (event, index) => {
        this.setState({ selectedIndex: index, anchorEl: null });

        if(index == 1) {
            console.log("this is the firt one");
        }
      };

      handleClose = () => {
        this.setState({ anchorEl: null });
      };

      showDetails = () => {
          let Post = this.props.post
          console.log(Post.name, Post.img)
        }



    render() {
        const { anchorEl, showDetails, avatar, name, thumbnail, gallery, liked, findUserLike } = this.props;

        return (


        <div onClick={showDetails}> 
            <div className="imgPost Card"> 
                <div className="controls"> 
                    <div>   
                        <Avatar facebookId={avatar} size="40" round={true} /> <span className="Name"> {name}</span>
                    </div>

                    <div> 
                        <i onClick={findUserLike} className="material-icons"> {liked ? 'favorite_border' : 'favorite'} </i> 
                        <i className="material-icons">redo</i>
                        <i className="material-icons"> 
                        <Menu  id="lock-menu" anchorEl={anchorEl} open={Boolean(anchorEl)} onClose={this.handleClose}>
                        <MenuItem  selected={1 === this.state.selectedIndex} onClick={this.handleClose}> Share </MenuItem>
                        <MenuItem  selected={2 === this.state.selectedIndex} onClick={this.handleClose} > Delete </MenuItem>
                        <MenuItem  selected={3 === this.state.selectedIndex} onClick={this.handleClose}> <Link to="/tree/familyHub">Turn Comments Off </Link>  </MenuItem>
                        <MenuItem  selected={4 === this.state.selectedIndex} onClick={this.handleClose}> <Link to="/tree/familyHub">Copy Link </Link>  </MenuItem>
                        </Menu>
                        </i> 
                    </div>
                </div>

                    <div className="img_container">
                        <div className="gallery_indicator"> 
                            <span> <i className="material-icons"> {gallery ? 'collections' : ''} </i></span>
                        </div> 

                        <img src={thumbnail} alt=""/> 
                    </div>
                </div>
            </div>

        )
    }
}


    PostItem.propTypes = {
    showDetails: PropTypes.func.isRequired,
    findUserLike: PropTypes.func.isRequired,
    avatar: PropTypes.string.isRequired,
    name: PropTypes.string.isRequired,
    thumbnail: PropTypes.string.isRequired,
    gallery: PropTypes.bool.isRequired,
    liked: PropTypes.bool.isRequired
    };


export default PostItem;
