import React, { Component } from 'react'
import Avatar from 'react-avatar';
import PropTypes from 'prop-types';
// import MenuItem from '@material-ui/core/MenuItem';
// import Menu from '@material-ui/core/Menu';
import { Link } from 'react-router-dom';


class PostItem extends Component {







    render() {
        const { deletePost, showDetails, avatar, name, thumbnail, gallery, liked, findUserLike, commentItem } = this.props;
        const { anchorEl } = this.state;


        // const ImageContent = () => {
        //     if(props.thumbnail.length > 0) 
        //         return  ( 
        //         <img className="Card"  src={props.thumbnail} alt="" /> 
        //         )  

        //          return (
        //           <div> 
        //               <input ref={props.fileInputRef} style={{display: 'none'}}  type="file" onChange={props.fileSelectedHandler}/>
        //               <div className=""  onClick={props.fileInput}> 
        //                 <span>
        //                   <button onClick={props.fileUploadHandler} className="btn waves-effect waves-light" >Add Image
        //                       <i className="material-icons right">add_a_photo</i>
        //                   </button>
        //                 </span> 
        //               </div>
        //           </div>
        //         )
        // }



        return (


        <div onClick={showDetails}> 
            <div className="imgPost Card"> 
                <div className="controls"> 
                    <div>   
                        <Avatar facebookId={avatar} size="40" round={true} /> <span className="Name"> {name}</span>
                    </div>

                    <div> 
                        <i onClick={findUserLike} className="material-icons"> {liked ? 'favorite_border' : 'favorite'} </i> 
                        <i className="material-icons">redo</i>

                       {/* <a  aria-haspopup="true" aria-controls="lock-menu" onClick={this.handleClickListItem}>
                        <i className="material-icons">more_horiz</i></a>
                        <Menu  id="lock-menu" anchorEl={anchorEl} open={Boolean(anchorEl)} onClose={this.handleClose}>
                        <MenuItem  selected={1 === this.state.selectedIndex} onClick={this.handleClose}> Share </MenuItem>
                        <MenuItem  selected={2 === this.state.selectedIndex} onClick={this.handleClose}> <Link to="/tree/familyHub">Turn Comments Off </Link>  </MenuItem>
                        <MenuItem  selected={3 === this.state.selectedIndex} onClick={this.handleClose}> <Link to="/tree/familyHub">Copy Link </Link>  </MenuItem>
                        <MenuItem  selected={4 === this.state.selectedIndex} onClick={deletePost} > Delete </MenuItem>
                        </Menu> */}

                    </div>
                </div>

                    <div className="img_container">
                        <div className="gallery_indicator"> 
                            <span> <i className="material-icons"> {gallery ? 'collections' : ''} </i></span>
                        </div> 

                        <img src={thumbnail} alt=""/> 

                    </div>
                </div>
            </div>

        )
    }
}


    PostItem.propTypes = {
    deletePost: PropTypes.func.isRequired,
    showDetails: PropTypes.func.isRequired,
    findUserLike: PropTypes.func.isRequired,
    avatar: PropTypes.string.isRequired,
    name: PropTypes.string.isRequired,
    thumbnail: PropTypes.string.isRequired,
    gallery: PropTypes.bool.isRequired,
    liked: PropTypes.bool.isRequired
    };


export default PostItem;
