import { FETCH_POSTS, POST_SELECTED } from  '../actions/types';

const initialState = {
    items: [],
    item: items[0].name,
}

export default function(state = initialState, action) {
    switch(action.type) {
        case FETCH_POSTS:
            return {
                ...state,
                items: action.payload
            };

            case POST_SELECTED:
            return {
              ...state,
              item: action.payload,
            };

        default:
            return state;
    }
}
