import axios from 'axios';
import { FETCH_POSTS } from './types';

export const fetchPosts = () => dispatch => {
        fetch('http://jsonplaceholder.typicode.com/posts')
        .then(res => res.json())
        .then(posts => dispatch({
            type: FETCH_POSTS,
            payload: posts
        }) );
    }
