import React from 'react';
import EditableInput from "../common/editableInput";
import { deletePost } from '../../actions/postActions';


class TargetDetails extends Component {

  onDeletePost(id) {
    console.log(id)
    this.props.deletePost(id)
}

  render() {
      return (
        <div>
        <div className="TargetDetails">  
             <button 
             className="waves-effect waves-light btn" 
             onClick={this.onDeletePost.bind(this, props.postId)}> Delete 
             </button>
         <div> 
           <h3>{props.title}</h3>
         </div>

         <div className="Thumbnail"> 
           <img className="Card"  src={props.thumbnail} alt=""/> 

         </div>


       <div className="Description"> 
           <EditableInput {...props}/>
       </div>

         <div className="input-field col s12">
           <form onSubmit={props.handleCommentSubmit}>
           <i className="material-icons prefix">textsms</i>
           <input type="text" placeholder="Comment..." className="autocomplete" value={props.newComment} onChange={props.handleCommentSubmitChange}/>
           </form>
         </div>
       </div>
     </div>
      )
  }
}



const mapStateToProps = state => ({

});

export default connect(mapStateToProps, { deletePost })(TargetDetails);
